const PREFIX = 'connect:';

export const getApiKey = (packedInfo: string | null): string | null => {
    if (packedInfo && packedInfo.substr(0, 8) === PREFIX) {
        return packedInfo.split(':')[1];
    }

    return null;
};

export const getPackedApiKey = (apiKey: string | null): string | null => {
    if (apiKey) {
        return `${PREFIX}${apiKey}`;
    }

    return null;
};
