//import * as moment from 'moment'
import moment from 'moment';

export { moment }

export class SkewAdjustedTime {
    static clockSkewMs = 0

    static moment(): moment.Moment {
        return moment().add(SkewAdjustedTime.clockSkewMs, 'ms')
    }

    static now(): number {
        return moment.now() + SkewAdjustedTime.clockSkewMs;
    }

    static nowUtc(): number {
        return moment(SkewAdjustedTime.now()).utc().valueOf()
    }
}


